import React, { createContext, useContext, useEffect, useState } from 'react'
import dataService from '../services/dataService'

const AuthContext = createContext({})

export const useAuth = () => {
  const context = useContext(AuthContext)
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider')
  }
  return context
}

export const AuthProvider = ({ children, setError }) => {
  const [user, setUser] = useState(null)
  const [userProfile, setUserProfile] = useState(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    // Check for existing session in localStorage
    const checkExistingSession = async () => {
      try {
        const sessionData = localStorage.getItem('neenu_auth_session');
        if (sessionData) {
          const session = JSON.parse(sessionData);
          const user = dataService.getUser(session.userId);
          if (user) {
            setUser(user);
            setUserProfile(user);
          }
        }
      } catch (error) {
        console.error('Error checking session:', error);
      } finally {
        setLoading(false);
      }
    };

    checkExistingSession();
  }, [])

  const signIn = async (email, password) => {
    try {
      setLoading(true);
      const user = dataService.authenticate(email, password);

      if (user) {
        setUser(user);
        setUserProfile(user);

        // Save session
        localStorage.setItem('neenu_auth_session', JSON.stringify({
          userId: user.id,
          timestamp: Date.now()
        }));

        return { user, error: null };
      } else {
        return { user: null, error: { message: 'Invalid credentials' } };
      }
    } catch (error) {
      return { user: null, error };
    } finally {
      setLoading(false);
    }
  };

  const signOut = async () => {
    try {
      setUser(null);
      setUserProfile(null);
      localStorage.removeItem('neenu_auth_session');
      return { error: null };
    } catch (error) {
      return { error };
    }
  };

  const isAdmin = () => {
    return user?.role === 'admin';
  };

  const signUp = async (userData) => {
    try {
      setLoading(true);

      // Check if user already exists
      const existingUser = dataService.getUserByEmail(userData.email);
      if (existingUser) {
        return { user: null, error: { message: 'User already exists with this email' } };
      }

      // Create new user
      const newUser = dataService.addUser({
        name: userData.name,
        email: userData.email,
        password: userData.password,
        role: 'customer',
        phone: userData.phone || '',
        memberSince: new Date().toISOString().split('T')[0],
        totalOrders: 0,
        totalSpent: 0,
        loyaltyPoints: 0,
        totalSaved: 0,
        isActive: true
      });

      if (newUser) {
        setUser(newUser);
        setUserProfile(newUser);

        // Save session
        localStorage.setItem('neenu_auth_session', JSON.stringify({
          userId: newUser.id,
          timestamp: Date.now()
        }));

        return { user: newUser, error: null };
      } else {
        return { user: null, error: { message: 'Failed to create user' } };
      }
    } catch (error) {
      return { user: null, error };
    } finally {
      setLoading(false);
    }
  };

  const updateProfile = async (updates) => {
    try {
      if (!user) return { error: { message: 'No user logged in' } };

      const updatedUser = { ...user, ...updates };
      setUser(updatedUser);
      setUserProfile(updatedUser);

      // In a real app, you'd update the database here
      return { error: null };
    } catch (error) {
      return { error };
    }
  };

  const value = {
    user,
    userProfile,
    loading,
    signIn,
    signUp,
    signOut,
    isAdmin,
    updateProfile
  }

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  )
}

