src/pages/admin-panel/components/Dashboa.content:
import React, { useState, useEffect } from 'react';
import { 
  Package, Users, ShoppingCart, DollarSign, TrendingUp, TrendingDown, 
  AlertTriangle, Plus, BarChart3, Calendar, Eye, RefreshCw 
} from 'lucide-react';
import dataService from '../../../services/dataService';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalProducts: 0,
    totalUsers: 0,
    totalOrders: 0,
    totalRevenue: 0,
    recentOrders: [],
    lowStockProducts: [],
    monthlyRevenue: 0,
    weeklyOrders: 0,
    topSellingProducts: [],
    pendingOrders: 0,
    completedOrders: 0,
    averageOrderValue: 0
  });
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);

      // Get all data
      const productsResponse = await dataService.getProducts();
      const users = dataService.getUsers(); // Direct call since it's synchronous
      const orders = dataService.getOrders(); // Direct call since it's synchronous

      const products = productsResponse.data || [];
      const customerUsers = users.filter(u => u.role === 'customer');

      // Calculate revenue metrics
      const totalRevenue = orders.reduce((sum, order) => sum + (order.total || 0), 0);
      const averageOrderValue = orders.length > 0 ? totalRevenue / orders.length : 0;

      // Calculate date-based metrics
      const now = new Date();
      const currentMonth = now.getMonth();
      const currentWeek = getWeekNumber(now);

      const monthlyOrders = orders.filter(order => {
        const orderDate = new Date(order.createdAt);
        return orderDate.getMonth() === currentMonth && orderDate.getFullYear() === now.getFullYear();
      });

      const weeklyOrders = orders.filter(order => {
        const orderDate = new Date(order.createdAt);
        return getWeekNumber(orderDate) === currentWeek && orderDate.getFullYear() === now.getFullYear();
      });

      const monthlyRevenue = monthlyOrders.reduce((sum, order) => sum + (order.total || 0), 0);

      // Order status counts
      const pendingOrders = orders.filter(order => order.status === 'pending').length;
      const completedOrders = orders.filter(order => order.status === 'delivered' || order.status === 'completed').length;

      // Product analytics
      const lowStockProducts = products.filter(p => (p.stockQuantity || 0) < 10);
      const recentOrders = orders.slice(-5).reverse();

      // Top selling products (based on order frequency)
      const productSales = {};
      orders.forEach(order => {
        if (order.items) {
          order.items.forEach(item => {
            const productId = item.productId || item.id;
            if (productId) {
              productSales[productId] = (productSales[productId] || 0) + (item.quantity || 1);
            }
          });
        }
      });

      const topSellingProducts = Object.entries(productSales)
        .sort(([,a], [,b]) => b - a)
        .slice(0, 5)
        .map(([productId, quantity]) => {
          const product = products.find(p => p.id == productId);
          return product ? { ...product, soldQuantity: quantity } : null;
        })
        .filter(Boolean);

      setStats({
        totalProducts: products.length,
        totalUsers: customerUsers.length,
        totalOrders: orders.length,
        totalRevenue,
        monthlyRevenue,
        weeklyOrders: weeklyOrders.length,
        averageOrderValue,
        pendingOrders,
        completedOrders,
        recentOrders,
        lowStockProducts,
        topSellingProducts
      });

    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  const getWeekNumber = (date) => {
    const d = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));
    const dayNum = d.getUTCDay() || 7;
    d.setUTCDate(d.getUTCDate() + 4 - dayNum);
    const yearStart = new Date(Date.UTC(d.getUTCFullYear(),0,1));
    return Math.ceil((((d - yearStart) / 86400000) + 1)/7);
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await loadDashboardData();
  };

  const handleQuickRestockProduct = async (productId) => {
    const restockAmount = prompt('Enter quantity to add to stock:');
    if (restockAmount && !isNaN(restockAmount) && parseInt(restockAmount) > 0) {
      try {
        const products = await dataService.getProducts();
        const product = products.data.find(p => p.id === productId);
        if (product) {
          const newStock = (product.stockQuantity || 0) + parseInt(restockAmount);
          // In a real app, you'd call dataService.updateProduct
          // For now, we'll just update the local data and show success
          product.stockQuantity = newStock;
          product.inStock = newStock > 0;

          await loadDashboardData(); // Refresh data
          alert(`Stock updated! ${product.name} now has ${newStock} items in stock.`);
        }
      } catch (error) {
        console.error('Error updating stock:', error);
        alert('Failed to update stock. Please try again.');
      }
    }
  };

  const handleBulkRestock = () => {
    // Show modal or navigate to bulk stock management
    alert('Bulk restock feature - would open a dedicated stock management interface');
  };

  const StatCard = ({ title, value, subtitle, icon: Icon, color = 'primary' }) => (
    <div className="bg-card border border-border rounded-lg p-6 hover:shadow-lg transition-shadow">
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <p className="text-sm font-body text-muted-foreground">{title}</p>
          <p className="text-2xl font-heading font-bold text-foreground mt-1">{value}</p>
          {subtitle && (
            <p className="text-xs text-muted-foreground mt-1">{subtitle}</p>
          )}
        </div>
        <div className={`p-3 rounded-full bg-${color}/10 flex-shrink-0`}>
          <Icon className={`w-6 h-6 text-${color}`} />
        </div>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <RefreshCw className="w-8 h-8 animate-spin text-primary mx-auto mb-2" />
          <p className="text-muted-foreground">Loading dashboard data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-heading font-bold text-foreground">Dashboard</h1>
          <p className="text-muted-foreground">Welcome back! Here's what's happening with your store.</p>
        </div>
        <button
          onClick={handleRefresh}
          disabled={refreshing}
          className="flex items-center space-x-2 px-4 py-2 bg-primary text-primary-foreground rounded-lg hover:bg-primary/90 transition-colors disabled:opacity-50"
        >
          <RefreshCw className={`w-4 h-4 ${refreshing ? 'animate-spin' : ''}`} />
          <span>Refresh</span>
        </button>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Products"
          value={stats.totalProducts}
          subtitle={`${stats.lowStockProducts.length} low stock`}
          icon={Package}
          color="primary"
        />
        <StatCard
          title="Total Customers"
          value={stats.totalUsers}
          subtitle="Active users"
          icon={Users}
          color="success"
        />
        <StatCard
          title="Total Orders"
          value={stats.totalOrders}
          subtitle={`${stats.pendingOrders} pending`}
          icon={ShoppingCart}
          color="warning"
        />
        <StatCard
          title="Total Revenue"
          value={`â‚¹${stats.totalRevenue.toLocaleString()}`}
          subtitle={`Avg: â‚¹${Math.round(stats.averageOrderValue)}`}
          icon={DollarSign}
          color="success"
        />
      </div>

      {/* Secondary Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Monthly Revenue"
          value={`â‚¹${stats.monthlyRevenue.toLocaleString()}`}
          subtitle="This month"
          icon={BarChart3}
          color="primary"
        />
        <StatCard
          title="Weekly Orders"
          value={stats.weeklyOrders}
          subtitle="This week"
          icon={Calendar}
          color="success"
        />
        <StatCard
          title="Pending Orders"
          value={stats.pendingOrders}
          subtitle="Need attention"
          icon={AlertTriangle}
          color="warning"
        />
        <StatCard
          title="Completed Orders"
          value={stats.completedOrders}
          subtitle="Successfully delivered"
          icon={ShoppingCart}
          color="success"
        />
      </div>

      {/* Dashboard Sections - 2x2 Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Revenue Overview */}
        <div className="bg-card border border-border rounded-lg p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-heading font-semibold text-foreground">Revenue Overview</h2>
            <BarChart3 className="w-5 h-5 text-muted-foreground" />
          </div>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <span className="text-sm text-muted-foreground">Total Revenue</span>
              <span className="font-semibold text-foreground">â‚¹{stats.totalRevenue.toLocaleString()}</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm text-muted-foreground">This Month</span>
              <span className="font-semibold text-primary">â‚¹{stats.monthlyRevenue.toLocaleString()}</span>
            </div>
            <div className="w-full bg-muted rounded-full h-3">
              <div 
                className="bg-primary h-3 rounded-full transition-all duration-300"
                style={{ 
                  width: `${stats.totalRevenue > 0 ? Math.min((stats.monthlyRevenue / stats.totalRevenue) * 100, 100) : 0}%` 
                }}
              ></div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="p-3 bg-success/10 rounded-lg text-center">
                <p className="text-sm text-muted-foreground">Avg Order</p>
                <p className="font-semibold text-success">â‚¹{Math.round(stats.averageOrderValue)}</p>
              </div>
              <div className="p-3 bg-primary/10 rounded-lg text-center">
                <p className="text-sm text-muted-foreground">Completion Rate</p>
                <p className="font-semibold text-primary">
                  {stats.totalOrders > 0 ? Math.round((stats.completedOrders / stats.totalOrders) * 100) : 0}%
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Top Selling Products */}
        <div className="bg-card border border-border rounded-lg p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-heading font-semibold text-foreground">Top Selling</h2>
            <Eye className="w-5 h-5 text-muted-foreground" />
          </div>
          <div className="space-y-3">
            {stats.topSellingProducts.length > 0 ? (
              stats.topSellingProducts.slice(0, 5).map((product) => (
                <div key={product.id} className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                  <div className="flex-1">
                    <p className="font-body font-medium text-foreground">{product.name}</p>
                    <p className="text-sm text-muted-foreground">{product.weight || 'N/A'}</p>
                  </div>
                  <div className="text-right">
                    <p className="font-body font-bold text-primary">{product.soldQuantity} sold</p>
                    <p className="text-sm text-success">â‚¹{product.price}</p>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8">
                <Eye className="w-8 h-8 text-muted-foreground mx-auto mb-2" />
                <p className="text-muted-foreground">No sales data yet</p>
              </div>
            )}
          </div>
        </div>
        {/* Stock Alerts */}
        <div className="bg-card border border-border rounded-lg p-6">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center">
              <AlertTriangle className="w-5 h-5 mr-2 text-warning" />
              <h2 className="text-lg font-heading font-semibold text-foreground">Stock Alerts</h2>
              {stats.lowStockProducts.length > 0 && (
                <span className="ml-2 bg-warning text-warning-foreground px-2 py-1 rounded-full text-xs font-medium">
                  {stats.lowStockProducts.length}
                </span>
              )}
            </div>
            <button
              onClick={handleBulkRestock}
              className="flex items-center space-x-2 px-3 py-1 bg-primary/10 text-primary rounded-md text-sm hover:bg-primary/20 transition-colors"
            >
              <Plus className="w-4 h-4" />
              <span>+ Bulk</span>
            </button>
          </div>
          <div className="space-y-3">
            {stats.lowStockProducts.length > 0 ? (
              stats.lowStockProducts.slice(0, 5).map((product) => (
                <div key={product.id} className="flex items-center justify-between p-3 bg-warning/10 rounded-lg">
                  <div className="flex-1">
                    <p className="font-body font-medium text-foreground">{product.name}</p>
                    <p className="text-sm text-muted-foreground">{product.weight || 'N/A'}</p>
                  </div>
                  <div className="flex items-center space-x-3">
                    <div className="text-right">
                      <p className={`font-body font-bold ${
                        (product.stockQuantity || 0) <= 5 ? 'text-destructive' : 'text-warning'
                      }`}>
                        {product.stockQuantity || 0} left
                      </p>
                    </div>
                    <button
                      onClick={() => handleQuickRestockProduct(product.id)}
                      className="px-3 py-1 bg-primary text-primary-foreground rounded hover:bg-primary/90 transition-colors text-sm"
                    >
                      + Add
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8">
                <Package className="w-8 h-8 text-success mx-auto mb-2" />
                <p className="text-success font-medium">All products well stocked!</p>
              </div>
            )}
          </div>
        </div>

        {/* Recent Orders */}
        <div className="bg-card border border-border rounded-lg p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-heading font-semibold text-foreground">Recent Orders</h2>
            <ShoppingCart className="w-5 h-5 text-muted-foreground" />
          </div>
          <div className="space-y-3">
            {stats.recentOrders.length > 0 ? (
              stats.recentOrders.slice(0, 5).map((order) => (
                <div key={order.id} className="flex items-center justify-between p-3 bg-muted/50 rounded-lg hover:bg-muted/70 transition-colors">
                  <div className="flex-1">
                    <p className="font-body font-medium text-foreground">#{order.id}</p>
                    <p className="text-sm text-muted-foreground">
                      {new Date(order.createdAt).toLocaleDateString('en-IN')}
                    </p>
                    <p className="text-sm text-muted-foreground">
                      {order.shippingAddress?.firstName || order.customerName || 'Customer'}
                    </p>
                  </div>
                  <div className="text-right">
                    <p className="font-body font-bold text-foreground">â‚¹{(order.total || 0).toLocaleString()}</p>
                    <span className={`inline-block px-3 py-1 rounded-full text-xs font-medium capitalize mt-1 ${
                      order.status === 'pending' ? 'bg-warning/20 text-warning' :
                      order.status === 'processing' ? 'bg-primary/20 text-primary' :
                      order.status === 'shipped' ? 'bg-blue-100 text-blue-600' :
                      order.status === 'delivered' || order.status === 'completed' ? 'bg-success/20 text-success' :
                      order.status === 'cancelled' ? 'bg-destructive/20 text-destructive' :
                      'bg-muted text-muted-foreground'
                    }`}>
                      {order.status}
                    </span>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8">
                <ShoppingCart className="w-8 h-8 text-muted-foreground mx-auto mb-2" />
                <p className="text-muted-foreground">No orders yet</p>
              </div>
            )}
          </div>
        </div>

      </div>
    </div>
  );
};

export default Dashboard;

src/pages/admin-panel/components/OrderMa.content:
import React, { useState, useEffect } from 'react';
import { Search, Eye, Package, Truck, CheckCircle, Download, Printer } from 'lucide-react';
import dataService from '../../../services/dataService';
import { Button } from '../../../components/ui/Button';
import { Input } from '../../../components/ui/Input';
import { downloadInvoice, printInvoice } from '../../../utils/invoiceGenerator';

const OrderManagement = () => {
  const [orders, setOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = () => {
    setLoading(true);
    const allOrders = dataService.getOrders();
    setOrders(allOrders);
    setLoading(false);
  };

  const filteredOrders = orders.filter(order => {
    const matchesSearch = order.id.toString().includes(searchTerm) ||
                         order.shippingAddress?.name?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = !statusFilter || order.status === statusFilter;
    return matchesSearch && matchesStatus;
  });

  const updateOrderStatus = (orderId, newStatus) => {
    dataService.updateOrder(orderId, { status: newStatus });
    loadOrders();
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending':
        return <Package className="w-4 h-4 text-warning" />;
      case 'processing':
        return <Package className="w-4 h-4 text-primary" />;
      case 'shipped':
        return <Truck className="w-4 h-4 text-primary" />;
      case 'delivered':
        return <CheckCircle className="w-4 h-4 text-success" />;
      default:
        return <Package className="w-4 h-4 text-muted-foreground" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending':
        return 'text-warning bg-warning/10';
      case 'processing':
        return 'text-primary bg-primary/10';
      case 'shipped':
        return 'text-primary bg-primary/10';
      case 'delivered':
        return 'text-success bg-success/10';
      case 'cancelled':
        return 'text-destructive bg-destructive/10';
      default:
        return 'text-muted-foreground bg-muted/10';
    }
  };

  const handleDownloadInvoice = (order) => {
    try {
      const settings = dataService.getSettings();
      const customer = dataService.getUser(order.userId) || { 
        name: order.customerName, 
        email: order.customerEmail, 
        phone: order.customerPhone 
      };
      downloadInvoice(order, customer, settings);
    } catch (error) {
      console.error('Error downloading invoice:', error);
      alert('Failed to download invoice. Please try again.');
    }
  };

  const handlePrintInvoice = (order) => {
    try {
      const settings = dataService.getSettings();
      const customer = dataService.getUser(order.userId) || { 
        name: order.customerName, 
        email: order.customerEmail, 
        phone: order.customerPhone 
      };
      printInvoice(order, customer, settings);
    } catch (error) {
      console.error('Error printing invoice:', error);
      alert('Failed to print invoice. Please try again.');
    }
  };

  if (loading) {
    return <div className="flex items-center justify-center h-64">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-heading font-bold text-foreground">Order Management</h1>
        <p className="text-muted-foreground">Track and manage customer orders</p>
      </div>

      {/* Filters */}
      <div className="bg-card border border-border rounded-lg p-4">
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground" size={20} />
              <Input
                placeholder="Search by order ID or customer name..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>
          </div>
          <div className="sm:w-48">
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="w-full h-10 px-3 rounded-md border border-border bg-background text-foreground"
            >
              <option value="">All Status</option>
              <option value="pending">Pending</option>
              <option value="processing">Processing</option>
              <option value="shipped">Shipped</option>
              <option value="delivered">Delivered</option>
              <option value="cancelled">Cancelled</option>
            </select>
          </div>
        </div>
      </div>

      {/* Orders List */}
      <div className="bg-card border border-border rounded-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-muted/50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                  Order
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                  Customer
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                  Total
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                  Date
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="divide-y divide-border">
              {filteredOrders.map((order) => (
                <tr key={order.id} className="hover:bg-muted/50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div>
                      <div className="text-sm font-medium text-foreground">#{order.id}</div>
                      <div className="text-sm text-muted-foreground">
                        {order.items?.length} item(s)
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div>
                      <div className="text-sm font-medium text-foreground">
                        {order.shippingAddress?.name}
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {order.shippingAddress?.city}
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-foreground">â‚¹{order.total}</div>
                    <div className="text-sm text-muted-foreground capitalize">
                      {order.paymentMethod}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(order.status)}`}>
                      {getStatusIcon(order.status)}
                      <span className="ml-1 capitalize">{order.status}</span>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-muted-foreground">
                    {new Date(order.createdAt).toLocaleDateString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center space-x-2">
                      <select
                        value={order.status}
                        onChange={(e) => updateOrderStatus(order.id, e.target.value)}
                        className="text-xs px-2 py-1 rounded border border-border bg-background"
                      >
                        <option value="pending">Pending</option>
                        <option value="processing">Processing</option>
                        <option value="shipped">Shipped</option>
                        <option value="delivered">Delivered</option>
                        <option value="cancelled">Cancelled</option>
                      </select>
                      <button
                        onClick={() => handleDownloadInvoice(order)}
                        className="p-1 text-primary hover:text-primary/80 transition-colors"
                        title="Download Invoice"
                      >
                        <Download className="w-4 h-4" />
                      </button>
                      <button
                        onClick={() => handlePrintInvoice(order)}
                        className="p-1 text-primary hover:text-primary/80 transition-colors"
                        title="Print Invoice"
                      >
                        <Printer className="w-4 h-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {filteredOrders.length === 0 && (
        <div className="text-center py-12">
          <p className="text-muted-foreground">No orders found matching your criteria.</p>
        </div>
      )}
    </div>
  );
};

export default OrderManagement;

src/pages/admin-panel/components/Product.content:
import React, { useState, useEffect } from 'react';
import { X } from 'lucide-react';
import dataService from '../../../services/dataService';
import { Button } from '../../../components/ui/Button';
import { Input } from '../../../components/ui/Input';

const ProductForm = ({ product, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    originalPrice: '',
    category: '',
    subcategory: '',
    image: '',
    weight: '',
    stockQuantity: '',
    ingredients: '',
    benefits: '',
    inStock: true
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (product) {
      setFormData({
        ...product,
        price: product.price.toString(),
        originalPrice: product.originalPrice.toString(),
        stockQuantity: product.stockQuantity.toString(),
        ingredients: product.ingredients?.join(', ') || '',
        benefits: product.benefits?.join(', ') || ''
      });
    }
  }, [product]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        originalPrice: parseFloat(formData.originalPrice),
        stockQuantity: parseInt(formData.stockQuantity),
        ingredients: formData.ingredients.split(',').map(item => item.trim()).filter(Boolean),
        benefits: formData.benefits.split(',').map(item => item.trim()).filter(Boolean),
        rating: product?.rating || 4.5,
        reviewCount: product?.reviewCount || 0,
        badges: product?.badges || []
      };

      if (product) {
        dataService.updateProduct(product.id, productData);
      } else {
        dataService.addProduct(productData);
      }

      onSave();
    } catch (err) {
      setError('Failed to save product. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const categories = [
    { id: 'oils-ghee', name: 'Oils & Ghee' },
    { id: 'spices-masalas', name: 'Spices & Masalas' },
    { id: 'natural-sweeteners', name: 'Natural Sweeteners' },
    { id: 'dairy-products', name: 'Dairy Products' },
    { id: 'pickles-preserves', name: 'Pickles & Preserves' },
    { id: 'sweets-desserts', name: 'Sweets & Desserts' }
  ];

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setFormData(prev => ({
          ...prev,
          image: event.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
      <div className="bg-card border border-border rounded-lg w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between p-6 border-b border-border">
          <h2 className="text-xl font-heading font-bold text-foreground">
            {product ? 'Edit Product' : 'Add New Product'}
          </h2>
          <button
            onClick={onCancel}
            className="p-2 hover:bg-muted rounded-full transition-colors"
          >
            <X size={20} />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-foreground mb-1">
                Product Name *
              </label>
              <Input
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
                placeholder="Enter product name"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-foreground mb-1">
                Weight/Size *
              </label>
              <Input
                name="weight"
                value={formData.weight}
                onChange={handleChange}
                required
                placeholder="e.g., 500ml, 250g"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-foreground mb-1">
              Description *
            </label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              rows={3}
              className="w-full px-3 py-2 border border-border rounded-md bg-background text-foreground"
              placeholder="Enter product description"
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-foreground mb-1">
                Price *
              </label>
              <Input
                name="price"
                type="number"
                value={formData.price}
                onChange={handleChange}
                required
                placeholder="0.00"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-foreground mb-1">
                Original Price
              </label>
              <Input
                name="originalPrice"
                type="number"
                value={formData.originalPrice}
                onChange={handleChange}
                placeholder="0.00"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-foreground mb-1">
                Stock Quantity *
              </label>
              <Input
                name="stockQuantity"
                type="number"
                value={formData.stockQuantity}
                onChange={handleChange}
                required
                placeholder="0"
              />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-foreground mb-1">
                Category *
              </label>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
                className="w-full h-10 px-3 rounded-md border border-border bg-background text-foreground"
              >
                <option value="">Select Category</option>
                {categories.map((category, index) => (
                  <option key={index} value={category.id}>{category.name}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-foreground mb-1">
                Subcategory
              </label>
              <Input
                name="subcategory"
                value={formData.subcategory}
                onChange={handleChange}
                placeholder="Enter subcategory"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-foreground mb-1">
              Product Image
            </label>
            <div className="space-y-2">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="w-full px-3 py-2 border border-border rounded-md bg-background text-foreground file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:bg-primary file:text-primary-foreground hover:file:bg-primary/90"
              />
              {formData.image && (
                <div className="mt-2">
                  <img
                    src={formData.image}
                    alt="Preview"
                    className="w-20 h-20 object-cover rounded-md border"
                  />
                </div>
              )}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-foreground mb-1">
              Ingredients (comma-separated)
            </label>
            <Input
              name="ingredients"
              value={formData.ingredients}
              onChange={handleChange}
              placeholder="Organic coconut, Sea salt, etc."
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-foreground mb-1">
              Benefits (comma-separated)
            </label>
            <Input
              name="benefits"
              value={formData.benefits}
              onChange={handleChange}
              placeholder="Rich in vitamins, Natural antioxidants, etc."
            />
          </div>

          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              name="inStock"
              checked={formData.inStock}
              onChange={handleChange}
              className="w-4 h-4 text-primary border-border rounded"
            />
            <label className="text-sm font-medium text-foreground">
              In Stock
            </label>
          </div>

          {error && (
            <div className="bg-destructive/10 border border-destructive/20 rounded-lg p-3">
              <p className="text-sm text-destructive">{error}</p>
            </div>
          )}

          <div className="flex justify-end space-x-3 pt-4">
            <Button type="button" variant="outline" onClick={onCancel}>
              Cancel
            </Button>
            <Button type="submit" disabled={loading}>
              {loading ? 'Saving...' : 'Save Product'}
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductForm;

import React, { useState, useEffect } from 'react';
import { Plus, Edit, Trash2, Search, Filter } from 'lucide-react';
import dataService from '../../../services/dataService';
import { Button } from '../../../components/ui/Button';
import { Input } from '../../../components/ui/Input';
import ProductForm from './ProductForm';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [showProductForm, setShowProductForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      setLoading(true);
      const response = await dataService.getProducts();
      setProducts(response.data);
    } catch (error) {
      console.error('Error loading products:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = !selectedCategory || product.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const handleAddProduct = () => {
    setEditingProduct(null);
    setShowProductForm(true);
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setShowProductForm(true);
  };

  const handleDeleteProduct = (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      dataService.deleteProduct(productId);
      loadProducts();
    }
  };

  const handleProductSaved = () => {
    setShowProductForm(false);
    setEditingProduct(null);
    loadProducts();
  };

  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const loadCategories = async () => {
      try {
        const response = await dataService.getCategories();
        setCategories(response.data);
      } catch (error) {
        console.error('Error loading categories:', error);
      }
    };
    loadCategories();
  }, []);

  if (loading) {
    return <div className="flex items-center justify-center h-64">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-2xl font-heading font-bold text-foreground">Product Management</h1>
          <p className="text-muted-foreground">Manage your product catalog</p>
        </div>
        <Button onClick={handleAddProduct} className="flex items-center space-x-2">
          <Plus size={20} />
          <span>Add Product</span>
        </Button>
      </div>

      {/* Filters */}
      <div className="bg-card border border-border rounded-lg p-4">
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground" size={20} />
              <Input
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>
          </div>
          <div className="sm:w-48">
            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="w-full h-10 px-3 rounded-md border border-border bg-background text-foreground"
            >
              <option value="" key="all-categories">All Categories</option>
              {categories.map((category, index) => (
                <option key={index} value={category.id || category}>
                  {category.name || category}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Products Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredProducts.map((product) => (
          <div key={product.id} className="bg-card border border-border rounded-lg overflow-hidden">
            <div className="aspect-square bg-muted relative">
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-full object-cover"
                onError={(e) => {
                  e.target.src = '/assets/images/no_image.png';
                }}
              />
              <div className="absolute top-2 right-2 flex space-x-2">
                <button
                  onClick={() => handleEditProduct(product)}
                  className="p-2 bg-card/80 backdrop-blur-sm rounded-full hover:bg-card transition-colors"
                >
                  <Edit size={16} className="text-primary" />
                </button>
                <button
                  onClick={() => handleDeleteProduct(product.id)}
                  className="p-2 bg-card/80 backdrop-blur-sm rounded-full hover:bg-card transition-colors"
                >
                  <Trash2 size={16} className="text-destructive" />
                </button>
              </div>
            </div>
            <div className="p-4">
              <h3 className="font-body font-medium text-foreground line-clamp-2 mb-2">
                {product.name}
              </h3>
              <div className="flex items-center justify-between mb-2">
                <div className="flex items-center space-x-2">
                  <span className="text-lg font-bold text-foreground">â‚¹{product.price}</span>
                  {product.originalPrice > product.price && (
                    <span className="text-sm text-muted-foreground line-through">
                      â‚¹{product.originalPrice}
                    </span>
                  )}
                </div>
                <span className="text-sm text-muted-foreground">{product.weight}</span>
              </div>
              <div className="flex items-center justify-between text-sm">
                <span className={`px-2 py-1 rounded-full ${
                  product.inStock ? 'bg-success/10 text-success' : 'bg-destructive/10 text-destructive'
                }`}>
                  {product.inStock ? 'In Stock' : 'Out of Stock'}
                </span>
                <span className="text-muted-foreground">Stock: {product.stockQuantity}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {filteredProducts.length === 0 && (
        <div className="text-center py-12">
          <p className="text-muted-foreground">No products found matching your criteria.</p>
        </div>
      )}

      {/* Product Form Modal */}
      {showProductForm && (
        <ProductForm
          product={editingProduct}
          onSave={handleProductSaved}
          onCancel={() => setShowProductForm(false)}
        />
      )}
    </div>
  );
};

export default ProductManagement;

src/pages/admin-panel/components/Setting.content:
import React, { useState, useEffect } from 'react';
import { Save } from 'lucide-react';
import dataService from '../../../services/dataService';
import { Button } from '../../../components/ui/Button';
import { Input } from '../../../components/ui/Input';

const Settings = () => {
  const [settings, setSettings] = useState({
    siteName: '',
    currency: '',
    shippingFee: '',
    freeShippingThreshold: '',
    taxRate: ''
  });
  const [loading, setLoading] = useState(false);
  const [saved, setSaved] = useState(false);

  useEffect(() => {
    loadSettings();
  }, []);

  const loadSettings = () => {
    const currentSettings = dataService.getSettings();
    setSettings({
      siteName: currentSettings.siteName || '',
      currency: currentSettings.currency || '',
      shippingFee: currentSettings.shippingFee?.toString() || '',
      freeShippingThreshold: currentSettings.freeShippingThreshold?.toString() || '',
      taxRate: (currentSettings.taxRate * 100)?.toString() || ''
    });
  };

  const handleChange = (e) => {
    setSettings({
      ...settings,
      [e.target.name]: e.target.value
    });
    setSaved(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    const updatedSettings = {
      siteName: settings.siteName,
      currency: settings.currency,
      shippingFee: parseFloat(settings.shippingFee),
      freeShippingThreshold: parseFloat(settings.freeShippingThreshold),
      taxRate: parseFloat(settings.taxRate) / 100
    };

    dataService.updateSettings(updatedSettings);
    setLoading(false);
    setSaved(true);

    setTimeout(() => setSaved(false), 3000);
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-heading font-bold text-foreground">Settings</h1>
        <p className="text-muted-foreground">Configure your store settings</p>
      </div>

      <div className="bg-card border border-border rounded-lg p-6">
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-foreground mb-2">
                Site Name
              </label>
              <Input
                name="siteName"
                value={settings.siteName}
                onChange={handleChange}
                placeholder="Your Store Name"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-foreground mb-2">
                Currency
              </label>
              <Input
                name="currency"
                value={settings.currency}
                onChange={handleChange}
                placeholder="INR"
              />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div>
              <label className="block text-sm font-medium text-foreground mb-2">
                Shipping Fee (â‚¹)
              </label>
              <Input
                name="shippingFee"
                type="number"
                value={settings.shippingFee}
                onChange={handleChange}
                placeholder="50"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-foreground mb-2">
                Free Shipping Threshold (â‚¹)
              </label>
              <Input
                name="freeShippingThreshold"
                type="number"
                value={settings.freeShippingThreshold}
                onChange={handleChange}
                placeholder="500"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-foreground mb-2">
                Tax Rate (%)
              </label>
              <Input
                name="taxRate"
                type="number"
                step="0.01"
                value={settings.taxRate}
                onChange={handleChange}
                placeholder="18"
              />
            </div>
          </div>

          <div className="flex items-center justify-between pt-6 border-t border-border">
            {saved && (
              <div className="text-sm text-success">Settings saved successfully!</div>
            )}
            <Button
              type="submit"
              disabled={loading}
              className="flex items-center space-x-2 ml-auto"
            >
              <Save size={16} />
              <span>{loading ? 'Saving...' : 'Save Settings'}</span>
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Settings;

