              <div className="flex items-center space-x-3">
                <div className={`w-10 h-10 rounded-lg flex items-center justify-center ${
                  selectedOption === option?.id ? 'bg-primary text-primary-foreground' : 'bg-muted text-muted-foreground'
                }`}>
                  <Icon name={option?.icon} size={20} />
                </div>
                <div>
                  <h3 className="font-body font-medium text-foreground">
                    {option?.name}
                  </h3>
                  <p className="font-body text-sm text-muted-foreground">
                    {option?.description}
                  </p>
                </div>
              </div>

              <div className="text-right">
                <p className="font-data font-semibold text-foreground">
                  {option?.price === 0 ? 'Free' : `â‚¹${option?.price}`}
                </p>
                <div className={`w-5 h-5 rounded-full border-2 flex items-center justify-center mt-1 ${
                  selectedOption === option?.id
                    ? 'border-primary bg-primary' :'border-border'
                }`}>
                  {selectedOption === option?.id && (
                    <div className="w-2 h-2 bg-primary-foreground rounded-full" />
                  )}
                </div>
              </div>
            </div>
          </label>
        ))}

        {/* Delivery Information */}
        <div className="bg-accent/10 border border-accent/20 rounded-lg p-4 mt-6">
          <div className="flex items-start space-x-3">
            <Icon name="Info" size={16} className="text-accent mt-0.5" />
            <div className="space-y-2">
              <h4 className="font-body font-medium text-foreground">
                Delivery Information
              </h4>
              <ul className="font-body text-sm text-muted-foreground space-y-1">
                <li>â€¢ Orders placed before 2 PM are processed the same day</li>
                <li>â€¢ Weekend deliveries available for express and premium options</li>
                <li>â€¢ Free delivery on orders above â‚¹{isBengaluru ? '499' : '999'}</li>
                <li>â€¢ All products are carefully packaged to maintain freshness</li>
              </ul>
            </div>
          </div>
        </div>

        <div className="flex justify-between pt-6">
          <Button
            type="button"
            variant="outline"
            onClick={onBack}
            iconName="ArrowLeft"
            iconPosition="left"
          >
            Back to Shipping
          </Button>
          <Button
            type="submit"
            variant="default"
            iconName="ArrowRight"
            iconPosition="right"
            disabled={!selectedOption}
          >
            Continue to Payment
          </Button>
        </div>
      </form>
    </div>
  );
};

export default DeliveryOptions;

