          <div className="flex items-start space-x-3">
            <Icon name="Info" size={16} className="text-primary mt-0.5" />
            <div className="space-y-2">
              <h4 className="font-body font-medium text-foreground">
                Order Confirmation
              </h4>
              <ul className="font-body text-sm text-muted-foreground space-y-1">
                <li>â€¢ You will receive an order confirmation email shortly</li>
                <li>â€¢ Track your order status in your account dashboard</li>
                <li>â€¢ Contact support for any questions about your order</li>
                <li>â€¢ Return policy applies for 7 days from delivery</li>
              </ul>
            </div>
          </div>
        </div>

        {/* Terms and Conditions */}
        <div className="text-center">
          <p className="font-body text-sm text-muted-foreground">
            By placing this order, you agree to our{' '}
            <a href="/terms" className="text-primary hover:underline">
              Terms of Service
            </a>{' '}
            and{' '}
            <a href="/privacy" className="text-primary hover:underline">
              Privacy Policy
            </a>
          </p>
        </div>

        {/* Action Buttons */}
        <div className="flex justify-between pt-6">
          <Button
            type="button"
            variant="outline"
            onClick={() => onBack && onBack(3)}
            iconName="ArrowLeft"
            iconPosition="left"
            disabled={isProcessing}
          >
            Back to Payment
          </Button>
          <Button
            type="button"
            variant="default"
            onClick={handlePlaceOrder}
            loading={isProcessing}
            iconName="CheckCircle"
            iconPosition="right"
            className="min-w-[140px]"
          >
            {isProcessing ? 'Processing...' : 'Place Order'}
          </Button>
        </div>
      </div>
    </div>
  );
};

