import React, { useState } from 'react';
import Image from '../../../components/AppImage';
import Input from '../../../components/ui/Input';
import Button from '../../../components/ui/Button';
import Icon from '../../../components/AppIcon';

const OrderSummary = ({ 
  cartItems = [], 
  subtotal = 0, 
  shipping = 0, 
  discount = 0, 
  total = 0,
  onApplyCoupon,
  appliedCoupon = null,
  isCollapsible = false 
}) => {
  const [isExpanded, setIsExpanded] = useState(!isCollapsible);
  const [couponCode, setCouponCode] = useState('');
  const [couponError, setCouponError] = useState('');

  const mockCartItems = [
    {
      id: 1,
      name: 'Organic Turmeric Powder',
      variant: '250g',
      price: 299,
      originalPrice: 349,
      quantity: 2,
      image: 'https://images.unsplash.com/photo-1615485290382-441e4d049cb5?w=400&h=400&fit=crop'
    },
    {
      id: 2,
      name: 'Cold Pressed Coconut Oil',
      variant: '500ml',
      price: 450,
      originalPrice: 500,
      quantity: 1,
      image: 'https://images.unsplash.com/photo-1474979266404-7eaacbcd87c5?w=400&h=400&fit=crop'
    },
    {
      id: 3,
      name: 'Himalayan Pink Salt',
      variant: '1kg',
      price: 199,
      originalPrice: 249,
      quantity: 1,
      image: 'https://images.unsplash.com/photo-1518843875459-f738682238a6?w=400&h=400&fit=crop'
    }
  ];

  const items = cartItems?.length > 0 ? cartItems : mockCartItems;
  const calculatedSubtotal = subtotal || items?.reduce((sum, item) => sum + (item?.price * item?.quantity), 0);
  const calculatedShipping = shipping || (calculatedSubtotal >= 499 ? 0 : 49);
  const calculatedDiscount = discount || (appliedCoupon === 'FLAT10' && calculatedSubtotal >= 1499 ? calculatedSubtotal * 0.1 : 0);
  const calculatedTotal = total || (calculatedSubtotal + calculatedShipping - calculatedDiscount);

  const handleApplyCoupon = () => {
    setCouponError('');

    if (!couponCode?.trim()) {
      setCouponError('Please enter a coupon code');
      return;
    }

    if (couponCode?.toUpperCase() === 'FLAT10') {
      if (calculatedSubtotal >= 1499) {
        if (onApplyCoupon) {
          onApplyCoupon(couponCode?.toUpperCase());
        }
        setCouponCode('');
      } else {
        setCouponError('Minimum order value â‚¹1,499 required for FLAT10');
      }
    } else {
      setCouponError('Invalid coupon code');
    }
  };

  const handleRemoveCoupon = () => {
    if (onApplyCoupon) {
      onApplyCoupon(null);
    }
  };

  return (
    <div className="bg-card border border-border rounded-lg overflow-hidden">
      {/* Header - Collapsible on mobile */}
      {isCollapsible && (
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="w-full p-4 flex items-center justify-between bg-muted/30 hover:bg-muted/50 transition-colors duration-200"
        >
          <div className="flex items-center space-x-2">
            <Icon name="ShoppingCart" size={20} />
            <span className="font-heading font-semibold text-foreground">
              Order Summary ({items?.length} items)
            </span>
          </div>
          <div className="flex items-center space-x-2">
            <span className="font-data font-semibold text-foreground">
              â‚¹{calculatedTotal?.toFixed(2)}
            </span>
            <Icon 
              name="ChevronDown" 
              size={16} 
              className={`transform transition-transform duration-200 ${isExpanded ? 'rotate-180' : ''}`}
            />
          </div>
        </button>
      )}
      {/* Content */}
      {isExpanded && (
        <div className="p-4 space-y-4">
          {!isCollapsible && (
            <h3 className="font-heading font-semibold text-lg text-foreground">
              Order Summary
            </h3>
          )}

          {/* Cart Items */}
          <div className="space-y-3 max-h-64 overflow-y-auto">
            {items?.map((item) => (
              <div key={item?.id} className="flex items-center space-x-3">
                <div className="relative w-12 h-12 rounded-lg overflow-hidden flex-shrink-0">
                  <Image
                    src={item?.image}
                    alt={item?.name}
                    className="w-full h-full object-cover"
                  />
                  <div className="absolute -top-1 -right-1 bg-primary text-primary-foreground text-xs font-data font-bold rounded-full w-5 h-5 flex items-center justify-center">
                    {item?.quantity}
                  </div>
                </div>

                <div className="flex-1 min-w-0">
                  <h4 className="font-body font-medium text-sm text-foreground truncate">
                    {item?.name}
                  </h4>
                  <p className="font-caption text-xs text-muted-foreground">
                    {item?.variant}
                  </p>
                </div>

                <div className="text-right">
                  <p className="font-data font-semibold text-sm text-foreground">
                    â‚¹{(item?.price * item?.quantity)?.toFixed(2)}
                  </p>
                  {item?.originalPrice && item?.originalPrice > item?.price && (
                    <p className="font-data text-xs text-muted-foreground line-through">
                      â‚¹{(item?.originalPrice * item?.quantity)?.toFixed(2)}
                    </p>
                  )}
                </div>
              </div>
            ))}
          </div>

          {/* Coupon Section */}
          <div className="border-t border-border pt-4">
            {appliedCoupon ? (
              <div className="flex items-center justify-between p-3 bg-success/10 border border-success/20 rounded-lg">
                <div className="flex items-center space-x-2">
                  <Icon name="Tag" size={16} className="text-success" />
                  <span className="font-body font-medium text-success">
                    {appliedCoupon} Applied
                  </span>
                </div>
                <button
                  onClick={handleRemoveCoupon}
                  className="text-success hover:text-success/80 transition-colors duration-200"
                >
                  <Icon name="X" size={16} />
                </button>
              </div>
            ) : (
              <div className="space-y-2">
                <div className="flex space-x-2">
                  <Input
                    type="text"
                    placeholder="Enter coupon code"
                    value={couponCode}
                    onChange={(e) => setCouponCode(e?.target?.value?.toUpperCase())}
                    error={couponError}
                    className="flex-1"
                  />
                  <Button
                    type="button"
                    variant="outline"
                    onClick={handleApplyCoupon}
                    disabled={!couponCode?.trim()}
                  >
                    Apply
                  </Button>
                </div>
                {calculatedSubtotal >= 1499 && !appliedCoupon && (
                  <p className="font-body text-xs text-success">
                    Use code FLAT10 for 10% off!
                  </p>
                )}
              </div>
            )}
          </div>

          {/* Order Totals */}
          <div className="border-t border-border pt-4 space-y-2">
            <div className="flex justify-between font-body text-sm">
              <span className="text-muted-foreground">Subtotal</span>
              <span className="font-data font-medium">â‚¹{calculatedSubtotal?.toFixed(2)}</span>
            </div>

            <div className="flex justify-between font-body text-sm">
              <span className="text-muted-foreground">Shipping</span>
              <span className="font-data font-medium">
                {calculatedShipping === 0 ? 'Free' : `â‚¹${calculatedShipping?.toFixed(2)}`}
              </span>
            </div>

            {calculatedDiscount > 0 && (
              <div className="flex justify-between font-body text-sm">
                <span className="text-success">Discount ({appliedCoupon})</span>
                <span className="font-data font-medium text-success">
                  -â‚¹{calculatedDiscount?.toFixed(2)}
                </span>
              </div>
            )}

            <div className="flex justify-between font-heading font-semibold text-base pt-2 border-t border-border">
              <span>Total</span>
              <span className="font-data">â‚¹{calculatedTotal?.toFixed(2)}</span>
            </div>
          </div>

          {/* Free Shipping Notice */}
          {calculatedSubtotal < 499 && (
            <div className="bg-warning/10 border border-warning/20 rounded-lg p-3">
              <p className="font-caption text-xs text-warning-foreground">
                Add â‚¹{(499 - calculatedSubtotal)?.toFixed(2)} more for free shipping!
              </p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default OrderSummary;

src/pages/checkout-process/components/Pa.content:
import React, { useState } from 'react';
import Input from '../../../components/ui/Input';
import Select from '../../../components/ui/Select';
import Button from '../../../components/ui/Button';
import { Checkbox } from '../../../components/ui/Checkbox';
import Icon from '../../../components/AppIcon';

const PaymentForm = ({ onNext, onBack, orderTotal }) => {
  const [paymentMethod, setPaymentMethod] = useState('');
  const [cardData, setCardData] = useState({
    cardNumber: '',
    expiryMonth: '',
    expiryYear: '',
    cvv: '',
    cardholderName: ''
  });
  const [upiId, setUpiId] = useState('');
  const [savePaymentMethod, setSavePaymentMethod] = useState(false);
  const [errors, setErrors] = useState({});

  const paymentMethods = [
    {
      id: 'cod',
      name: 'Cash on Delivery',
      description: 'Pay when your order is delivered',
      icon: 'Banknote',
      fee: 0,
      available: true
    },
    {
      id: 'upi',
      name: 'UPI Payment',
      description: 'Pay using Google Pay, PhonePe, Paytm, etc.',
      icon: 'Smartphone',
      fee: 0,
      available: true
    },
    {
      id: 'card',
      name: 'Credit/Debit Card',
      description: 'Visa, Mastercard, RuPay accepted',
      icon: 'CreditCard',
      fee: 0,
      available: true
    },
    {
      id: 'netbanking',
      name: 'Net Banking',
      description: 'Pay directly from your bank account',
      icon: 'Building2',
      fee: 0,
      available: true
    }
  ];

  const monthOptions = Array.from({ length: 12 }, (_, i) => ({
    value: String(i + 1)?.padStart(2, '0'),
    label: String(i + 1)?.padStart(2, '0')
  }));

  const yearOptions = Array.from({ length: 10 }, (_, i) => {
    const year = new Date()?.getFullYear() + i;
    return { value: String(year), label: String(year) };
  });

  const handleCardInputChange = (e) => {
    const { name, value } = e?.target;
    let formattedValue = value;

    if (name === 'cardNumber') {
      formattedValue = value?.replace(/\s/g, '')?.replace(/(.{4})/g, '$1 ')?.trim();
      if (formattedValue?.length > 19) return;
    } else if (name === 'cvv') {
      formattedValue = value?.replace(/\D/g, '');
      if (formattedValue?.length > 3) return;
    }

    setCardData(prev => ({ ...prev, [name]: formattedValue }));
    if (errors?.[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validatePaymentForm = () => {
    const newErrors = {};

    if (paymentMethod === 'card') {
      if (!cardData?.cardNumber?.replace(/\s/g, '')) {
        newErrors.cardNumber = 'Card number is required';
      } else if (cardData?.cardNumber?.replace(/\s/g, '')?.length < 16) {
        newErrors.cardNumber = 'Invalid card number';
      }

      if (!cardData?.expiryMonth) newErrors.expiryMonth = 'Expiry month is required';
      if (!cardData?.expiryYear) newErrors.expiryYear = 'Expiry year is required';
      if (!cardData?.cvv) newErrors.cvv = 'CVV is required';
      if (!cardData?.cardholderName?.trim()) newErrors.cardholderName = 'Cardholder name is required';
    } else if (paymentMethod === 'upi') {
      if (!upiId?.trim()) {
        newErrors.upiId = 'UPI ID is required';
      } else if (!/^[\w.-]+@[\w.-]+$/?.test(upiId)) {
        newErrors.upiId = 'Invalid UPI ID format';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors)?.length === 0;
  };

  const handleSubmit = (e) => {
    e?.preventDefault();
    if (!paymentMethod) {
      setErrors({ paymentMethod: 'Please select a payment method' });
      return;
    }

    if (validatePaymentForm()) {
      const paymentData = {
        method: paymentMethod,
        ...(paymentMethod === 'card' && { cardData }),
        ...(paymentMethod === 'upi' && { upiId }),
        savePaymentMethod
      };
      onNext(paymentData);
    }
  };

  return (
    <div className="bg-card border border-border rounded-lg p-6">
      <h2 className="font-heading font-semibold text-xl text-foreground mb-6">
        Payment Method
      </h2>
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Payment Methods */}
        <div className="space-y-3">
          {paymentMethods?.map((method) => (
            <label
              key={method?.id}
              className={`block p-4 border rounded-lg cursor-pointer transition-colors duration-200 ${
                paymentMethod === method?.id
                  ? 'border-primary bg-primary/5'
                  : method?.available 
                    ? 'border-border hover:border-primary/50' :'border-border bg-muted/50 cursor-not-allowed'
              }`}
            >
              <input
                type="radio"
                name="paymentMethod"
                value={method?.id}
                checked={paymentMethod === method?.id}
                onChange={(e) => setPaymentMethod(e?.target?.value)}
                disabled={!method?.available}
                className="sr-only"
              />

              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className={`w-10 h-10 rounded-lg flex items-center justify-center ${
                    paymentMethod === method?.id 
                      ? 'bg-primary text-primary-foreground' 
                      : method?.available
                        ? 'bg-muted text-muted-foreground'
                        : 'bg-muted/50 text-muted-foreground/50'
                  }`}>
                    <Icon name={method?.icon} size={20} />
                  </div>
                  <div>
                    <h3 className={`font-body font-medium ${
                      method?.available ? 'text-foreground' : 'text-muted-foreground'
                    }`}>
                      {method?.name}
                    </h3>
                    <p className="font-body text-sm text-muted-foreground">
                      {method?.description}
                    </p>
                  </div>
                </div>

                <div className="flex items-center space-x-3">
                  {method?.fee > 0 && (
                    <span className="font-data text-sm text-muted-foreground">
                      +â‚¹{method?.fee}
                    </span>
                  )}
                  <div className={`w-5 h-5 rounded-full border-2 flex items-center justify-center ${
                    paymentMethod === method?.id
                      ? 'border-primary bg-primary' :'border-border'
                  }`}>
                    {paymentMethod === method?.id && (
                      <div className="w-2 h-2 bg-primary-foreground rounded-full" />
                    )}
                  </div>
                </div>
              </div>
            </label>
          ))}
        </div>

        {errors?.paymentMethod && (
          <p className="text-destructive text-sm font-body">{errors?.paymentMethod}</p>
        )}

        {/* Card Payment Form */}
        {paymentMethod === 'card' && (
          <div className="space-y-4 p-4 bg-muted/30 rounded-lg border border-border">
            <h3 className="font-body font-medium text-foreground">
              Card Details
            </h3>

            <Input
              label="Cardholder Name"
              type="text"
              name="cardholderName"
              value={cardData?.cardholderName}
              onChange={handleCardInputChange}
              error={errors?.cardholderName}
              required
              placeholder="Name as on card"
            />

            <Input
              label="Card Number"
              type="text"
              name="cardNumber"
              value={cardData?.cardNumber}
              onChange={handleCardInputChange}
              error={errors?.cardNumber}
              required
              placeholder="1234 5678 9012 3456"
            />

            <div className="grid grid-cols-3 gap-4">
              <Select
                label="Month"
                options={monthOptions}
                value={cardData?.expiryMonth}
                onChange={(value) => setCardData(prev => ({ ...prev, expiryMonth: value }))}
                error={errors?.expiryMonth}
                required
                placeholder="MM"
              />
              <Select
                label="Year"
                options={yearOptions}
                value={cardData?.expiryYear}
                onChange={(value) => setCardData(prev => ({ ...prev, expiryYear: value }))}
                error={errors?.expiryYear}
                required
                placeholder="YYYY"
              />
              <Input
                label="CVV"
                type="text"
                name="cvv"
                value={cardData?.cvv}
                onChange={handleCardInputChange}
                error={errors?.cvv}
                required
                placeholder="123"
              />
            </div>
          </div>
        )}

        {/* UPI Payment Form */}
        {paymentMethod === 'upi' && (
          <div className="space-y-4 p-4 bg-muted/30 rounded-lg border border-border">
            <h3 className="font-body font-medium text-foreground">
              UPI Details
            </h3>

            <Input
              label="UPI ID"
              type="text"
              value={upiId}
              onChange={(e) => setUpiId(e?.target?.value)}
              error={errors?.upiId}
              required
              placeholder="yourname@paytm"
              description="Enter your UPI ID (e.g., 9876543210@paytm)"
            />
          </div>
        )}

        {/* COD Information */}
        {paymentMethod === 'cod' && (
          <div className="p-4 bg-warning/10 border border-warning/20 rounded-lg">
            <div className="flex items-start space-x-3">
              <Icon name="Info" size={16} className="text-warning mt-0.5" />
              <div>
                <h4 className="font-body font-medium text-foreground mb-2">
                  Cash on Delivery
                </h4>
                <ul className="font-body text-sm text-muted-foreground space-y-1">
                  <li>â€¢ Pay â‚¹{orderTotal?.toFixed(2)} when your order is delivered</li>
                  <li>â€¢ Please keep exact change ready</li>
                  <li>â€¢ COD available for orders up to â‚¹5,000</li>
                  <li>â€¢ Additional verification may be required</li>
                </ul>
              </div>
            </div>
          </div>
        )}

        {/* Save Payment Method */}
        {(paymentMethod === 'card' || paymentMethod === 'upi') && (
          <Checkbox
            label="Save this payment method for future orders"
            checked={savePaymentMethod}
            onChange={(e) => setSavePaymentMethod(e?.target?.checked)}
          />
        )}

        {/* Security Information */}
        <div className="bg-success/10 border border-success/20 rounded-lg p-4">
          <div className="flex items-start space-x-3">
            <Icon name="Shield" size={16} className="text-success mt-0.5" />
            <div>
              <h4 className="font-body font-medium text-foreground mb-2">
                Secure Payment
              </h4>
              <p className="font-body text-sm text-muted-foreground">
                Your payment information is encrypted and secure. We never store your card details.
              </p>
            </div>
          </div>
        </div>

        <div className="flex justify-between pt-6">
          <Button
            type="button"
            variant="outline"
            onClick={onBack}
            iconName="ArrowLeft"
            iconPosition="left"
          >
            Back to Delivery
          </Button>
          <Button
            type="submit"
            variant="default"
            iconName="ArrowRight"
            iconPosition="right"
          >
            Review Order
          </Button>
        </div>
      </form>
    </div>
  );
};

export default PaymentForm;

