                  onClick={() => handleQuantityChange(item?.quantity - 1)}
                  className="p-2 hover:bg-muted transition-colors duration-200 rounded-l-lg"
                  disabled={item?.quantity <= 1}
                >
                  <Icon name="Minus" size={16} />
