import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';

const ProfileManagement = ({ user, onUpdateProfile }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [isChangingPassword, setIsChangingPassword] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name,
    email: user?.email,
    phone: user?.phone,
    dateOfBirth: user?.dateOfBirth || '',
    gender: user?.gender || ''
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [errors, setErrors] = useState({});

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
    if (errors?.[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: ''
      }));
    }
  };

  const handlePasswordChange = (field, value) => {
    setPasswordData(prev => ({
      ...prev,
      [field]: value
    }));
    if (errors?.[field]) {
      setErrors(prev => ({
        ...prev,
        [field]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData?.name?.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData?.email?.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/?.test(formData?.email)) {
      newErrors.email = 'Please enter a valid email';
    }

    if (!formData?.phone?.trim()) {
      newErrors.phone = 'Phone number is required';
    } else if (!/^\d{10}$/?.test(formData?.phone?.replace(/\D/g, ''))) {
      newErrors.phone = 'Please enter a valid 10-digit phone number';
    }

    setErrors(newErrors);
    return Object.keys(newErrors)?.length === 0;
  };

  const validatePasswordForm = () => {
    const newErrors = {};

    if (!passwordData?.currentPassword) {
      newErrors.currentPassword = 'Current password is required';
    }

    if (!passwordData?.newPassword) {
      newErrors.newPassword = 'New password is required';
    } else if (passwordData?.newPassword?.length < 8) {
      newErrors.newPassword = 'Password must be at least 8 characters';
    }

    if (passwordData?.newPassword !== passwordData?.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors)?.length === 0;
  };

  const handleSaveProfile = () => {
    if (validateForm()) {
      onUpdateProfile(formData);
      setIsEditing(false);
    }
  };

  const handleChangePassword = () => {
    if (validatePasswordForm()) {
      // Handle password change
      console.log('Password change requested');
      setIsChangingPassword(false);
      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });
    }
  };

  const handleCancel = () => {
    setFormData({
      name: user?.name,
      email: user?.email,
      phone: user?.phone,
      dateOfBirth: user?.dateOfBirth || '',
      gender: user?.gender || ''
    });
    setIsEditing(false);
    setIsChangingPassword(false);
    setErrors({});
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <h1 className="font-heading text-2xl font-bold text-foreground">
          Profile Management
        </h1>
        {!isEditing && !isChangingPassword && (
          <Button
            variant="outline"
            onClick={() => setIsEditing(true)}
            iconName="Edit"
            iconPosition="left"
          >
            Edit Profile
          </Button>
        )}
      </div>
      {/* Profile Information */}
      <div className="bg-card border border-border rounded-lg p-6">
        <div className="flex items-center space-x-4 mb-6">
          <div className="w-20 h-20 bg-primary/10 rounded-full flex items-center justify-center">
            <Icon name="User" size={32} className="text-primary" />
          </div>
          <div>
            <h2 className="font-heading text-xl font-semibold text-foreground">
              {user?.name}
            </h2>
            <p className="font-body text-muted-foreground">
              Member since {user?.memberSince}
            </p>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <Input
            label="Full Name"
            type="text"
            value={formData?.name}
            onChange={(e) => handleInputChange('name', e?.target?.value)}
            disabled={!isEditing}
            error={errors?.name}
            required
          />

          <Input
            label="Email Address"
            type="email"
            value={formData?.email}
            onChange={(e) => handleInputChange('email', e?.target?.value)}
            disabled={!isEditing}
            error={errors?.email}
            required
          />

          <Input
            label="Phone Number"
            type="tel"
            value={formData?.phone}
            onChange={(e) => handleInputChange('phone', e?.target?.value)}
            disabled={!isEditing}
            error={errors?.phone}
            required
          />

          <Input
            label="Date of Birth"
            type="date"
            value={formData?.dateOfBirth}
            onChange={(e) => handleInputChange('dateOfBirth', e?.target?.value)}
            disabled={!isEditing}
          />

          <div className="md:col-span-2">
            <label className="block font-body font-medium text-foreground mb-2">
              Gender
            </label>
            <div className="flex space-x-4">
              {['Male', 'Female', 'Other']?.map((option) => (
                <label key={option} className="flex items-center space-x-2">
                  <input
                    type="radio"
                    name="gender"
                    value={option}
                    checked={formData?.gender === option}
                    onChange={(e) => handleInputChange('gender', e?.target?.value)}
                    disabled={!isEditing}
                    className="w-4 h-4 text-primary border-border focus:ring-primary"
                  />
                  <span className="font-body text-sm text-foreground">
                    {option}
                  </span>
                </label>
              ))}
            </div>
          </div>
        </div>

        {isEditing && (
          <div className="flex space-x-3 mt-6 pt-6 border-t border-border">
            <Button
              variant="default"
              onClick={handleSaveProfile}
            >
              Save Changes
            </Button>
            <Button
              variant="outline"
              onClick={handleCancel}
            >
              Cancel
            </Button>
          </div>
        )}
      </div>
      {/* Password Change Section */}
      <div className="bg-card border border-border rounded-lg p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="font-heading text-lg font-semibold text-foreground">
            Password & Security
          </h3>
          {!isChangingPassword && !isEditing && (
            <Button
              variant="outline"
              size="sm"
              onClick={() => setIsChangingPassword(true)}
            >
              Change Password
            </Button>
          )}
        </div>

        {isChangingPassword ? (
          <div className="space-y-4">
            <Input
              label="Current Password"
              type="password"
              value={passwordData?.currentPassword}
              onChange={(e) => handlePasswordChange('currentPassword', e?.target?.value)}
              error={errors?.currentPassword}
              required
            />

            <Input
              label="New Password"
              type="password"
              value={passwordData?.newPassword}
              onChange={(e) => handlePasswordChange('newPassword', e?.target?.value)}
              error={errors?.newPassword}
              description="Password must be at least 8 characters long"
              required
            />

            <Input
              label="Confirm New Password"
              type="password"
              value={passwordData?.confirmPassword}
              onChange={(e) => handlePasswordChange('confirmPassword', e?.target?.value)}
              error={errors?.confirmPassword}
              required
            />

            <div className="flex space-x-3 pt-4">
              <Button
                variant="default"
                onClick={handleChangePassword}
              >
                Update Password
              </Button>
              <Button
                variant="outline"
                onClick={handleCancel}
              >
                Cancel
              </Button>
            </div>
          </div>
        ) : (
          <div className="text-muted-foreground">
            <p className="font-body text-sm">
              Last password change: {user?.lastPasswordChange || 'Never'}
            </p>
          </div>
        )}
      </div>
      {/* Account Statistics */}
      <div className="bg-card border border-border rounded-lg p-6">
        <h3 className="font-heading text-lg font-semibold text-foreground mb-4">
          Account Statistics
        </h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div className="text-center">
            <div className="font-data text-2xl font-bold text-primary">
              {user?.totalOrders}
            </div>
            <div className="font-caption text-sm text-muted-foreground">
              Total Orders
            </div>
          </div>
          <div className="text-center">
            <div className="font-data text-2xl font-bold text-success">
              â‚¹{user?.totalSpent}
            </div>
            <div className="font-caption text-sm text-muted-foreground">
              Total Spent
            </div>
          </div>
          <div className="text-center">
            <div className="font-data text-2xl font-bold text-warning">
              {user?.loyaltyPoints}
            </div>
            <div className="font-caption text-sm text-muted-foreground">
              Loyalty Points
            </div>
          </div>
          <div className="text-center">
            <div className="font-data text-2xl font-bold text-destructive">
              â‚¹{user?.totalSaved}
            </div>
            <div className="font-caption text-sm text-muted-foreground">
              Total Saved
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileManagement;

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Image from '../../../components/AppImage';

const WishlistSection = ({ wishlistItems, onRemoveFromWishlist, onAddToCart }) => {
  const [sortBy, setSortBy] = useState('recent');

  const sortOptions = [
    { value: 'recent', label: 'Recently Added' },
    { value: 'name', label: 'Name A-Z' },
    { value: 'price-low', label: 'Price: Low to High' },
    { value: 'price-high', label: 'Price: High to Low' }
  ];

  const sortedItems = [...wishlistItems]?.sort((a, b) => {
    switch (sortBy) {
      case 'name':
        return a?.name?.localeCompare(b?.name);
      case 'price-low':
        return a?.price - b?.price;
      case 'price-high':
        return b?.price - a?.price;
      case 'recent':
      default:
        return new Date(b.addedDate) - new Date(a.addedDate);
    }
  });

  const handleAddToCart = (item) => {
    onAddToCart({
      id: item?.id,
      name: item?.name,
      price: item?.price,
      image: item?.image,
      variant: item?.selectedVariant || item?.variants?.[0],
      quantity: 1
    });
  };

  const calculateDiscount = (originalPrice, salePrice) => {
    return Math.round(((originalPrice - salePrice) / originalPrice) * 100);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <h1 className="font-heading text-2xl font-bold text-foreground">
          My Wishlist ({wishlistItems?.length})
        </h1>
        {wishlistItems?.length > 0 && (
          <div className="flex items-center space-x-2">
            <Icon name="ArrowUpDown" size={16} className="text-muted-foreground" />
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e?.target?.value)}
              className="font-body text-sm border border-border rounded-lg px-3 py-2 bg-background text-foreground focus:outline-none focus:ring-2 focus:ring-primary"
            >
              {sortOptions?.map((option) => (
                <option key={option?.value} value={option?.value}>
                  {option?.label}
                </option>
              ))}
            </select>
          </div>
        )}
      </div>
      {/* Wishlist Items */}
      {wishlistItems?.length === 0 ? (
        <div className="text-center py-12 bg-card border border-border rounded-lg">
          <Icon name="Heart" size={48} className="mx-auto text-muted-foreground mb-4" />
          <h3 className="font-heading font-semibold text-foreground mb-2">
            Your wishlist is empty
          </h3>
          <p className="font-body text-muted-foreground mb-4">
            Save your favorite products to buy them later.
          </p>
          <Link to="/product-collection-grid">
            <Button variant="default">
              Start Shopping
            </Button>
          </Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {sortedItems?.map((item) => (
            <div key={item?.id} className="bg-card border border-border rounded-lg overflow-hidden hover:shadow-warm-md transition-shadow duration-200">
              {/* Product Image */}
              <div className="relative aspect-square overflow-hidden">
                <Image
                  src={item?.image}
                  alt={item?.name}
                  className="w-full h-full object-cover hover:scale-105 transition-transform duration-300"
                />

                {/* Badges */}
                <div className="absolute top-2 left-2 flex flex-col space-y-1">
                  {item?.originalPrice > item?.price && (
                    <span className="bg-destructive text-destructive-foreground text-xs font-caption font-bold px-2 py-1 rounded">
                      {calculateDiscount(item?.originalPrice, item?.price)}% OFF
                    </span>
                  )}
                  {item?.badges?.map((badge, index) => (
                    <span
                      key={index}
                      className="bg-success text-success-foreground text-xs font-caption font-medium px-2 py-1 rounded"
                    >
                      {badge}
                    </span>
                  ))}
                </div>

                {/* Remove from Wishlist */}
                <button
                  onClick={() => onRemoveFromWishlist(item?.id)}
                  className="absolute top-2 right-2 w-8 h-8 bg-background/80 backdrop-blur-sm rounded-full flex items-center justify-center hover:bg-background transition-colors duration-200"
                  aria-label="Remove from wishlist"
                >
                  <Icon name="X" size={16} className="text-foreground" />
                </button>

                {/* Stock Status */}
                {!item?.inStock && (
                  <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
                    <span className="bg-destructive text-destructive-foreground text-sm font-caption font-medium px-3 py-1 rounded">
                      Out of Stock
                    </span>
                  </div>
                )}
              </div>

              {/* Product Info */}
              <div className="p-4">
                <Link
                  to={`/product-detail-page?id=${item?.id}`}
                  className="block hover:text-primary transition-colors duration-200"
                >
                  <h3 className="font-body font-medium text-foreground mb-2 line-clamp-2">
                    {item?.name}
                  </h3>
                </Link>

                {/* Variant Selection */}
                {item?.variants && item?.variants?.length > 1 && (
                  <div className="mb-3">
                    <p className="font-caption text-xs text-muted-foreground mb-1">
                      Size/Weight
                    </p>
                    <div className="flex flex-wrap gap-1">
                      {item?.variants?.slice(0, 3)?.map((variant, index) => (
                        <span
                          key={index}
                          className="text-xs font-caption px-2 py-1 bg-muted rounded border border-border"
                        >
                          {variant}
                        </span>
                      ))}
                      {item?.variants?.length > 3 && (
                        <span className="text-xs font-caption text-muted-foreground">
                          +{item?.variants?.length - 3} more
                        </span>
                      )}
                    </div>
                  </div>
                )}

                {/* Price */}
                <div className="flex items-center space-x-2 mb-3">
                  <span className="font-data font-bold text-foreground">
                    â‚¹{item?.price?.toFixed(2)}
                  </span>
                  {item?.originalPrice > item?.price && (
                    <span className="font-data text-sm text-muted-foreground line-through">
                      â‚¹{item?.originalPrice?.toFixed(2)}
                    </span>
                  )}
                </div>

                {/* Rating */}
                {item?.rating && (
                  <div className="flex items-center space-x-1 mb-3">
                    <div className="flex items-center">
                      {[...Array(5)]?.map((_, i) => (
                        <Icon
                          key={i}
                          name="Star"
                          size={12}
                          className={`${
                            i < Math.floor(item?.rating)
                              ? 'text-warning fill-current' :'text-muted-foreground'
                          }`}
                        />
                      ))}
                    </div>
                    <span className="font-caption text-xs text-muted-foreground">
                      ({item?.reviewCount})
                    </span>
                  </div>
                )}

                {/* Added Date */}
                <p className="font-caption text-xs text-muted-foreground mb-3">
                  Added on {new Date(item.addedDate)?.toLocaleDateString('en-IN')}
                </p>

                {/* Actions */}
                <div className="space-y-2">
                  <Button
                    variant="default"
                    size="sm"
                    fullWidth
                    onClick={() => handleAddToCart(item)}
                    disabled={!item?.inStock}
                    iconName="ShoppingCart"
                    iconPosition="left"
                  >
                    {item?.inStock ? 'Add to Cart' : 'Out of Stock'}
                  </Button>
                  <Link to={`/product-detail-page?id=${item?.id}`}>
                    <Button
                      variant="outline"
                      size="sm"
                      fullWidth
                    >
                      View Details
                    </Button>
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
      {/* Recommendations */}
      {wishlistItems?.length > 0 && (
        <div className="bg-card border border-border rounded-lg p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="font-heading text-lg font-semibold text-foreground">
              You might also like
            </h3>
            <Link to="/product-collection-grid">
              <Button variant="outline" size="sm">
                View All
              </Button>
            </Link>
          </div>
          <p className="font-body text-muted-foreground">
            Based on your wishlist, we recommend checking out our featured collections and new arrivals.
          </p>
        </div>
      )}
    </div>
  );
};

export default WishlistSection;

